namespace gamboamartin\comercial\controllers;

use gamboamartin\errores\errores;
use gamboamartin\system\links_menu;
use gamboamartin\system\system;
use gamboamartin\template\html;
use html\cat_sat_moneda_html;
use html\com_tipo_cambio_html;
use models\com_tipo_cambio;
use PDO;
use stdClass;

class controlador_com_tipo_cambio extends system {

    public function __construct(PDO $link, html $html = new \gamboamartin\template_1\html(), stdClass $paths_conf = new stdClass()){
        $modelo = new com_tipo_cambio(link: $link);

        $html_ = new com_tipo_cambio_html(html: $html);
        $obj_link = new links_menu($this->registro_id);
        parent::__construct(html:$html_, link: $link,modelo:  $modelo, obj_link: $obj_link, paths_conf: $paths_conf);

        $this->titulo_lista = 'Tipos de cambio';



    }

    public function alta(bool $header, bool $ws = false): array|string
    {
        $r_alta = parent::alta(header: false); // TODO: Change the autogenerated stub
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar template',data:  $r_alta, header: $header,ws:$ws);
        }
        $select = (new cat_sat_moneda_html(html: $this->html_base))->select_cat_sat_moneda_id(cols: 12,
            con_registros: true,id_selected: -1,link: $this->link);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar select',data:  $select, header: $header,ws:$ws);
        }

        $this->inputs->select = new stdClass();
        $this->inputs->select->cat_sat_moneda_id = $select;

        return $r_alta;
    }

    public function modifica(bool $header, bool $ws = false, string $breadcrumbs = '', bool $aplica_form = true,
                             bool $muestra_btn = true): array|string
    {
        $r_modifica = parent::modifica(header: false,ws: false,breadcrumbs:  $breadcrumbs,aplica_form:  $aplica_form,
            muestra_btn:  $muestra_btn); // TODO: Change the autogenerated stub
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar template',data:  $r_modifica, header: $header,ws:$ws);
        }

        $select = (new cat_sat_moneda_html(html: $this->html_base))->select_cat_sat_moneda_id(cols: 12,
            con_registros: true,id_selected: $this->row_upd->cat_sat_moneda_id,link: $this->link);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar select',data:  $select, header: $header,ws:$ws);
        }

        $this->inputs->select = new stdClass();
        $this->inputs->select->cat_sat_moneda_id = $select;

        return $r_modifica;
    }


}
