namespace controllers;

use gamboamartin\direccion_postal\models\dp_cp;
use gamboamartin\errores\errores;
use gamboamartin\system\links_menu;
use gamboamartin\system\system;
use gamboamartin\template_1\html;
use html\dp_cp_html;
use html\dp_estado_html;
use html\dp_municipio_html;
use PDO;
use stdClass;


class controlador_dp_cp extends system {

    public function __construct(PDO $link, stdClass $paths_conf = new stdClass()){
        $modelo = new dp_cp(link: $link);
        $html_base = new html();
        $html = new dp_cp_html(html: $html_base);
        $obj_link = new links_menu($this->registro_id);
        parent::__construct(html:$html, link: $link,modelo:  $modelo, obj_link: $obj_link, paths_conf: $paths_conf);

        $this->titulo_lista = 'Códigos Postáles';

    }

    public function alta(bool $header, bool $ws = false): array|string
    {
        $r_alta =  parent::alta(header: false); // TODO: Change the autogenerated stub
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar template', data: $r_alta,header: false,ws: false);
        }

        $this->inputs->select = new stdClass();

        $select = (new dp_estado_html(html: $this->html_base))->select_dp_estado_id(cols:6,con_registros: true,
            id_selected:-1,link: $this->link);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar select', data: $select,header: false,ws: false);
        }

        $this->inputs->select->dp_estado_id = $select;



        $select = (new dp_municipio_html(html: $this->html_base))->select_dp_municipio_id(cols:6,con_registros: false, id_selected:-1,
            link: $this->link);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar select', data: $select,header: false,ws: false);
        }

        $in_georeferencia = (new dp_cp_html(html: $this->html_base))->input(cols: 12,row_upd:  new stdClass(),value_vacio:  true, campo: "Georeferencia");

        if(errores::$error){
            $error = $this->errores->error(mensaje: 'Error al generar el input',data:  $in_georeferencia);
            print_r($error);
            die('Error');
        }

        $this->inputs->georeferencia = $in_georeferencia;
        $this->inputs->select->dp_municipio_id = $select;

        return $r_alta;
    }

    public function alta_bd(bool $header, bool $ws = false): array|stdClass
    {

        if(isset($_POST['dp_estado_id'])){
            unset($_POST['dp_estado_id']);
        }

        $r_alta =  parent::alta_bd(header: true); // TODO: Change the autogenerated stub
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al insertar', data: $r_alta,header: false,ws: false);
        }
        return $r_alta;

    }

    /**
     * Función que obtiene los campos de dp_pais, dp_estado, dp_municipio y dp_cp por medio de
     * un arreglo $keys con los nombres de sus respectivos campos.
     * La variable $salida llama a la función get_out con los parámetros $header, $keys y $ws.
     * En caso de presentarse un error, un if se encarga de capturarlo y mostrar la información correspondiente.
     * Finalmente se retorna la variable $salida.
     * @param bool $header
     * @param bool $ws
     * @return array|stdClass
     */
    public function get_cp(bool $header, bool $ws = true): array|stdClass
    {

        $keys['dp_pais'] = array('id','descripcion','codigo','codigo_bis');
        $keys['dp_estado'] = array('id','descripcion','codigo','codigo_bis');
        $keys['dp_municipio'] = array('id','descripcion','codigo','codigo_bis');
        $keys['dp_cp'] = array('id','descripcion','codigo','codigo_bis');

        $salida = $this->get_out(header: $header,keys: $keys, ws: $ws);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar salida',data:  $salida,header: $header,ws: $ws);

        }


        return $salida;


    }


}
