namespace gamboamartin\empleado\controllers;

use gamboamartin\errores\errores;
use gamboamartin\system\links_menu;
use gamboamartin\system\system;
use gamboamartin\template\html;

use html\em_cuenta_bancaria_html;
use gamboamartin\empleado\models\em_cuenta_bancaria;
use PDO;
use stdClass;

class controlador_em_cuenta_bancaria extends system {

    public function __construct(PDO $link, html $html = new \gamboamartin\template_1\html(),
                                stdClass $paths_conf = new stdClass()){
        $modelo = new em_cuenta_bancaria(link: $link);
        $html_ = new em_cuenta_bancaria_html(html: $html);
        $obj_link = new links_menu($this->registro_id);
        parent::__construct(html:$html_, link: $link,modelo:  $modelo, obj_link: $obj_link, paths_conf: $paths_conf);

        $this->titulo_lista = 'Cuenta Bancaria';
    }

    public function alta(bool $header, bool $ws = false): array|string
    {
        $r_alta =  parent::alta(header: false, ws: false); // TODO: Change the autogenerated stub
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar template',data:  $r_alta, header: $header,ws:$ws);
        }

        $keys_selects = array();
        $keys_selects['em_empleado_id'] = new stdClass();
        $keys_selects['em_empleado_id']->label = 'Empleado';
        $keys_selects['em_empleado_id']->cols = 6;

        $keys_selects['bn_sucursal_id'] = new stdClass();
        $keys_selects['bn_sucursal_id']->label = 'Sucursal Bancaria';
        $keys_selects['bn_sucursal_id']->cols = 6;

        $inputs = (new em_cuenta_bancaria_html(html: $this->html_base))->genera_inputs_alta(
            controler: $this, modelo: $this->modelo, link: $this->link,keys_selects: $keys_selects);
        if(errores::$error){
            $error = $this->errores->error(mensaje: 'Error al generar inputs',data:  $inputs);
            print_r($error);
            die('Error');
        }
        return $r_alta;
    }

    public function modifica(bool $header, bool $ws = false, string $breadcrumbs = '', bool $aplica_form = true,
                             bool $muestra_btn = true): array|string
    {
        $base = $this->base();
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al maquetar datos',data:  $base,
                header: $header,ws:$ws);
        }

        return $base->template;
    }

    private function base(stdClass $params = new stdClass()): array|stdClass
    {
        $r_modifica =  parent::modifica(header: false,aplica_form:  false); // TODO: Change the autogenerated stub
        if(errores::$error){
            return $this->errores->error(mensaje: 'Error al generar template',data:  $r_modifica);
        }

        $inputs = (new em_cuenta_bancaria_html(html: $this->html_base))->inputs_em_cuenta_bancaria (
            controlador:$this, params: $params);
        if(errores::$error){
            return $this->errores->error(mensaje: 'Error al inicializar inputs',data:  $inputs);
        }

        $data = new stdClass();
        $data->template = $r_modifica;
        $data->inputs = $inputs;

        return $data;
    }

    public function get_cuentas_bancarias (bool $header, bool $ws = true): array|stdClass
    {
        $keys['em_cuenta_bancaria'] = array("id",'descripcion','codigo','alias','descripcion_select','codigo_bis',
            'num_cuenta', 'clabe');

        $keys['em_empleado'] = array("id",'descripcion','codigo','alias','descripcion_select','codigo_bis',
            'nombre', 'ap','am','rfc','curp','nss');

        $keys['bn_banco'] = array("id",'descripcion','codigo','alias','descripcion_select','codigo_bis');

        $salida = $this->get_out(header: $header,keys: $keys, ws: $ws);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar salida',data:  $salida,header: $header,ws: $ws);
        }
        return $salida;
    }

}
