    public function alta_partida_bd(bool $header, bool $ws = false){

        $this->link->beginTransaction();

        $siguiente_view = (new actions())->init_alta_bd();
        if(errores::$error){
            $this->link->rollBack();
            return $this->retorno_error(mensaje: 'Error al obtener siguiente view', data: $siguiente_view,
                header:  $header, ws: $ws);
        }


        if(isset($_POST['guarda'])){
            unset($_POST['guarda']);
        }
        if(isset($_POST['btn_action_next'])){
            unset($_POST['btn_action_next']);
        }


        $registro = $_POST;
        $registro['fc_factura_id'] = $this->registro_id;

        $r_alta_partida_bd = (new fc_partida($this->link))->alta_registro(registro:$registro); // TODO: Change the autogenerated stub
        if(errores::$error){
            $this->link->rollBack();
            return $this->retorno_error(mensaje: 'Error al dar de alta partida',data:  $r_alta_partida_bd,
                header: $header,ws:$ws);
        }

        $this->link->commit();

        if($header){

            $retorno = (new actions())->retorno_alta_bd(registro_id:$this->registro_id,seccion: $this->tabla,
                siguiente_view: $siguiente_view);
            if(errores::$error){
                return $this->retorno_error(mensaje: 'Error al dar de alta registro', data: $r_alta_partida_bd,
                    header:  true, ws: $ws);
            }
            header('Location:'.$retorno);
            exit;
        }
        if($ws){
            header('Content-Type: application/json');
            echo json_encode($r_alta_partida_bd, JSON_THROW_ON_ERROR);
            exit;
        }

        return $r_alta_partida_bd;

    }

    /**
     * @param bool $header Si header se muestra result en http
     * @param bool $ws
     * @param string $breadcrumbs
     * @param bool $aplica_form
     * @param bool $muestra_btn
     * @return array|string
     */
    public function modifica(bool $header, bool $ws = false, string $breadcrumbs = '', bool $aplica_form = true,
                             bool $muestra_btn = true): array|string
    {
        $r_modifica =  parent::modifica(header: false,aplica_form:  false); // TODO: Change the autogenerated stub
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al generar template',data:  $r_modifica, header: $header,ws:$ws);
        }

        $sub_total = (new fc_factura($this->link))->get_factura_sub_total(fc_factura_id:
            $this->fc_factura_id);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al obtener sub_total',data:  $sub_total,
                header: $header,ws:$ws);
        }
        
        $this->row_upd->subtotal = $sub_total;
        $descuento = (new fc_factura($this->link))->get_factura_descuento(fc_factura_id:
            $this->fc_factura_id);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al obtener descuento',data:  $descuento,
                header: $header,ws:$ws);
        }
        $this->row_upd->descuento = $descuento;
        
        $imp_trasladados = (new fc_factura($this->link))->get_factura_imp_trasladados(fc_factura_id:
            $this->fc_factura_id);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al obtener imp_trasladados',data:  $imp_trasladados,
                header: $header,ws:$ws);
        }
        $this->row_upd->impuestos_trasladados = $imp_trasladados;
        
        $imp_retenidos = (new fc_factura($this->link))->get_factura_imp_retenidos(fc_factura_id:
            $this->fc_factura_id);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al obtener imp_retenidos',data:  $imp_retenidos,
                header: $header,ws:$ws);
        }
        $this->row_upd->impuestos_retenidos = $imp_retenidos;

        $inputs = (new fc_factura_html(html: $this->html_base))->inputs_fc_factura(controlador:$this);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al inicializar inputs',data:  $inputs, header: $header,ws:$ws);
        }

        $partidas = (new fc_partida($this->link))->partidas(fc_factura_id: $this->fc_factura_id);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al obtener sucursales',data:  $partidas, header: $header,ws:$ws);
        }

        foreach ($partidas->registros as $indice=>$partida){
            $partida = $this->data_partida_btn(partida:$partida);
            if(errores::$error){
                return $this->retorno_error(mensaje: 'Error al asignar botones',data:  $partida, header: $header,ws:$ws);
            }
            $partidas->registros[$indice] = $partida;

        }

        $this->partidas = $partidas;

        return $r_modifica;
    }

    public function lista(bool $header, bool $ws = false): array
    {
        $r_lista = parent::lista($header, $ws); // TODO: Change the autogenerated stub
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al maquetar datos',data:  $r_lista, header: $header,ws:$ws);
        }

        $registros = (new fc_factura($this->link))->registros(return_obj: true);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al maquetar registros',data:  $registros,
                header: $header,ws:$ws);
        }

        $registros = $this->maqueta_registros_lista(registros: $registros);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al maquetar registros',data:  $registros, header: $header,ws:$ws);
        }
        $this->registros = $registros;


        return $r_lista;
    }

    private function maqueta_registros_lista(array $registros): array
    {
        foreach ($registros as $indice=> $row){
            $row = $this->asigna_link_partida_row(row: $row);
            if(errores::$error){
                return $this->errores->error(mensaje: 'Error al maquetar row',data:  $row);
            }
            $registros[$indice] = $row;
        }
        return $registros;
    }

    private function asigna_link_partida_row(stdClass $row): array|stdClass
    {
        $keys = array('fc_factura_id');
        $valida = $this->validacion->valida_ids(keys: $keys,registro:  $row);
        if(errores::$error){
            return $this->errores->error(mensaje: 'Error al validar row',data:  $valida);
        }

        $link_genera_xml = $this->obj_link->link_con_id(accion:'genera_xml',registro_id:  $row->fc_factura_id,
            seccion:  $this->tabla);
        if(errores::$error){
            return $this->errores->error(mensaje: 'Error al genera link',data:  $link_genera_xml);
        }

        $link_partidas = $this->obj_link->link_con_id(accion:'partidas',registro_id:  $row->fc_factura_id,
            seccion:  $this->tabla);
        if(errores::$error){
            return $this->errores->error(mensaje: 'Error al genera link',data:  $link_partidas);
        }

        $link_modifica = $this->obj_link->link_con_id(accion:'modifica',registro_id:  $row->fc_factura_id,
            seccion:  $this->tabla);
        if(errores::$error){
            return $this->errores->error(mensaje: 'Error al genera link',data:  $link_modifica);
        }

        $link_elimina_bd = $this->obj_link->link_con_id(accion:'elimina_bd',registro_id:  $row->fc_factura_id,
            seccion:  $this->tabla);
        if(errores::$error){
            return $this->errores->error(mensaje: 'Error al genera link',data:  $link_elimina_bd);
        }

        $row->link_genera_xml = $link_genera_xml;
        $row->link_genera_xml_style = 'info';

        $row->link_partidas = $link_partidas;
        $row->link_partidas_style = 'info';

        $row->link_modifica = $link_modifica;

        $row->link_elimina_bd = $link_elimina_bd;

        return $row;
    }

    private function data_partida_btn(array $partida): array
    {

        $params['fc_partida_id'] = $partida['fc_partida_id'];

        $btn_elimina = $this->html_base->button_href(accion:'elimina_bd',etiqueta:  'Elimina',
            registro_id:  $partida['fc_partida_id'], seccion: 'fc_partida',style:  'danger');

        if(errores::$error){
            return $this->errores->error(mensaje: 'Error al generar btn',data:  $btn_elimina);
        }
        $partida['link_elimina'] = $btn_elimina;


        $btn_modifica = $this->html_base->button_href(accion:'modifica_partida',etiqueta:  'Modifica',
            registro_id:  $partida['fc_factura_id'], seccion: 'fc_factura',style:  'warning', params: $params);

        if(errores::$error){
            return $this->errores->error(mensaje: 'Error al generar btn',data:  $btn_elimina);
        }
        $partida['link_modifica'] = $btn_modifica;

        $btn_ve = $this->html_base->button_href(accion:'ve_partida',etiqueta:  'Ver',
            registro_id:  $partida['fc_factura_id'], seccion: 'fc_factura',style:  'info', params: $params);

        if(errores::$error){
            return $this->errores->error(mensaje: 'Error al generar btn',data:  $btn_elimina);
        }
        $partida['link_ve'] = $btn_ve;
        return $partida;
    }

    public function genera_xml(bool $header, bool $ws = false){
        $fc_factura = $this->modelo->registro(registro_id: $this->registro_id);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al obtener fc_factura',data:  $fc_factura, header: $header,ws:$ws);
        }
        $partidas = (new fc_partida($this->link))->partidas(fc_factura_id: $this->registro_id);
        if(errores::$error){
            return $this->retorno_error(mensaje: 'Error al obtener partidas',data:  $partidas, header: $header,ws:$ws);
        }

       // $total = (new fc_factura($this->link))->t

        $comprobante = array();
        $comprobante['lugar_expedicion'] = $fc_factura['dp_cp_descripcion'];
        $comprobante['tipo_de_comprobante'] = $fc_factura['cat_sat_tipo_de_comprobante_codigo'];
        $comprobante['moneda'] = $fc_factura['cat_sat_moneda_codigo'];



        $emisor = array();
        $emisor['rfc'] = $fc_factura['org_empresa_rfc'];
        $receptor = array();
        $receptor['rfc'] = $fc_factura['com_cliente_rfc'];


        $conceptos = array();

        $impuestos = array();


        $cfdi = new cfdis();

        $ingreso = $cfdi->ingreso(comprobante: $comprobante,conceptos:  $conceptos, emisor: $emisor,
            impuestos: $impuestos,receptor:  $receptor);

        print_r($ingreso);exit;




    }

